mapper.delete:
delete tbl_like
where b_no = #{b_no}
and user_id = #{user_id}

mapper.update:
update tbl_board set
	b_title = #{b_title},
	b_content = #{b_content, jdbcType=VARCHAR}
where b_no = #{b_no}

update tbl_board set
	b_viewcnt = b_viewcnt + 1
where b_no = #{b_no}

update tbl_board set
	comment_cnt = comment_cnt + #{count}
where b_no = #{b_no}

update tbl_board set
	like_count = like_count + #{count}
where b_no = #{b_no}

mapper.select:
select count(*) from tbl_like
where b_no = #{b_no}
and user_id = #{user_id}
